def prepare_data(votation_dict, values_arr, row_init=2, col_init=1):
    """Prepares the data using a dictionary and an array"""
    
    #Create the basic DataFrame and select the rows needed.
    df = pd.DataFrame(votation_dict).iloc[row_init:,col_init:]  
    
    #Change the axis labels of the df.
    df.set_axis([i for i in range(len(df.columns))], 
            axis=1, inplace=True)
    df.reset_index(drop=True, inplace=True)
    
    #Change all mispelled names using the array.
    for i in range(len(values_arr)):
        df.replace(values_arr[i][0], values_arr[i][1], inplace=True)
    
    #Take away the congressman who did not vote. This may be changed after.
    for i in range(1, len(df.columns),2):
        df.loc[(df[i] == 0) | (df[i] == 'LO') , i-1] = 'No vota'

    return df

