def generate_js_format(number):
    """Transform the index numbers from 0,1,2 to 001,002,003 for not having conflicts with JSON"""
    for i in range(1, number+1):
        if i < 10:
            yield "00" + str(i)
        elif i < 100:
            yield "0" + str(i)
        else:
            yield str(i)
            
